function attachCodeCopyListeners(){document.querySelectorAll("pre").forEach(t=>{t.addEventListener("click",async()=>{document.body.focus();try{await navigator.clipboard.writeText(t.innerText),t.setAttribute("data-tooltip","Copied!"),setTimeout(()=>t.removeAttribute("data-tooltip"),1e3),console.log("Text copied to clipboard.")}catch(t){console.error("Failed to copy text:",t)}})})}function initializeCodeCopyFeature(){attachCodeCopyListeners()}initializeCodeCopyFeature();